$(document).ready(function(){$("#login_form").submit(function(e){e.preventDefault(),$.ajax({url:"users/login",type:"POST",data:{username:$("#username").val(),password:$("#password").val()},success:function(e){"failed"==e?alert("Nom d'utilisateur et/ou mot de passe incorrect."):(alert("Connexion réussie."),window.location.href="home/index")}})}),$("#register_form").submit(function(e){e.preventDefault(),$.ajax({url:"users/register",type:"POST",data:{username:$("#username").val(),email:$("#email").val(),password:$("#password").val()},success:function(e){"exists"==e?alert("Cet utilisateur existe déjà."):"succeed"==e?(alert("Inscription réussie."),window.location.href="home/index"):alert("Une erreur s'est produite.")}})}),$("#post_form").submit(function(e){e.preventDefault(),$.ajax({url:"posts/add_post",type:"POST",data:new FormData(this),contentType:!1,cache:!1,processData:!1,success:function(e){"failed"==e?alert("Cet article existe déjà."):(alert("Nouvel article ajouté avec succès."),window.location.href="dashboard/posts")}})})});